mapper.select:
		select
		`id`,`order_id`,`user_id`,`task_id`,`total_amount`,`scores_deduction`,`discount`,`payment_amount`,`order_status`,`expire_time`,`agent_id`,`gmt_create`,`gmt_modify`
		from ddp_order_info

			 and id = #{id} 
			 and order_id = #{orderId} 
			 and user_id = #{userId} 
			 and task_id = #{taskId} 
			 and total_amount = #{totalAmount} 
			 and scores_deduction = #{scoresDeduction} 
			 and discount = #{discount} 
			 and payment_amount = #{paymentAmount} 
			 and order_status = #{orderStatus} 
			 and expire_time = #{expireTime} 
			 and agent_id = #{agentId} 
			 and gmt_create = #{gmtCreate} 
			 and gmt_modify = #{gmtModify} 


               and DATE_FORMAT(t1.gmt_create,'%Y-%m-%d')   >=  DATE_FORMAT(#{endTime},'%Y-%m-%d')



				order by ${sort} ${order}


				order by id desc



			limit #{offset}, #{limit}

select count(*) from ddp_order_info

	 and id = #{id} 
	 and order_id = #{orderId} 
	 and user_id = #{userId} 
	 and task_id = #{taskId} 
	 and total_amount = #{totalAmount} 
	 and scores_deduction = #{scoresDeduction} 
	 and discount = #{discount} 
	 and payment_amount = #{paymentAmount} 
	 and order_status = #{orderStatus} 
	 and expire_time = #{expireTime} 
	 and agent_id = #{agentId} 
	 and gmt_create = #{gmtCreate} 
	 and gmt_modify = #{gmtModify}

		select t1.*,
		t3.id as task_type_id,t3.platform,t3.task_type_desc,t3.task_type,t3.task_img,
		t2.assistant_type,t2.task_number,t2.completed_number,t2.task_result,t2.task_status,t2.gmt_create as task_create_gmt
		from ddp_order_info t1 inner join ddp_task_apply_info t2 on t1.task_id =
		t2.id
		inner join ddp_task_type_info t3 on t2.task_type_id = t3.id 

			 and id = #{id} 
			 and t1.order_id = #{orderId} 
			 and t1.user_id = #{userId} 
			 and t1.task_id = #{taskId} 
			 and t1.order_status = #{orderStatus} 
			 and t1.agent_id = #{agentId} 
			 t1.and gmt_create = #{gmtCreate} 
			 t1.and gmt_modify = #{gmtModify} 


               and DATE_FORMAT(t1.gmt_create,'%Y-%m-%d')   >=  DATE_FORMAT(#{endTime},'%Y-%m-%d')



				order by ${sort} ${order}


				order by t1.id desc



			limit #{offset}, #{limit}
