mapper.select:
select
u.`user_id`,u.`username`,u.`name`,u.`password`,u.`dept_id`,u.`email`,u.`mobile`,u.`status`,u.`user_id_create`,u.`gmt_create`,u.`gmt_modified`,u.`sex`,u.`birth`,u.`pic_id`,u.`live_address`,u.`hobby`,u.`province`,u.`city`,u.`district`,u.`segment_code` as segmentCode
,d.name as deptName
from sys_user u left join sys_dept d on u.dept_id = d.dept_id

    and u.user_id = #{userId}
    and u.username = #{username}
    and u.name = #{name}
    and u.password = #{password}
    and u.dept_id = #{deptId}
    and u.dept_id in

            #{item}


    and u.email = #{email}
    and u.mobile = #{mobile}
    and u.status = #{status}
    and u.user_id_create = #{userIdCreate}
    and u.gmt_create = #{gmtCreate}
    and u.gmt_modified = #{gmtModified}
    and u.sex = #{sex}
    and u.birth = #{birth}
    and u.pic_id = #{picId}
    and u.live_address = #{liveAddress}
    and u.hobby = #{hobby}
    and u.province = #{province}
    and u.city = #{city}
    and u.district = #{district}
    and u.segment_code = #{segmentCode}

        and (
        (u.name like concat(concat('%',#{queryWord}),'%'))
        or (u.username like concat(concat('%',#{queryWord}),'%'))
        or (u.email like concat(concat('%',#{queryWord}),'%'))
        )




        order by ${sort} ${order}


        order by u.user_id desc



    limit #{offset}, #{limit}

select count(*) from sys_user u left join sys_dept d on u.dept_id = d.dept_id

    and u.user_id = #{userId}
    and u.username = #{username}
    and u.name = #{name}
    and u.password = #{password}
    and u.dept_id = #{deptId}
    and u.dept_id in

            #{item}


    and u.email = #{email}
    and u.mobile = #{mobile}
    and u.status = #{status}
    and u.user_id_create = #{userIdCreate}
    and u.gmt_create = #{gmtCreate}
    and u.gmt_modified = #{gmtModified}
    and u.sex = #{sex}
    and u.birth = #{birth}
    and u.pic_id = #{picId}
    and u.live_address = #{liveAddress}
    and u.hobby = #{hobby}
    and u.province = #{province}
    and u.city = #{city}
    and u.district = #{district}
    and u.segment_code = #{segmentCode}

        and (
        (u.name like concat(concat('%',#{queryWord}),'%'))
        or (u.username like concat(concat('%',#{queryWord}),'%'))
        or (u.email like concat(concat('%',#{queryWord}),'%'))
        )

select `user_id`,`username`,u.`name`,`password`,u.`dept_id`,`email`,`mobile`,`status`,`user_id_create`,`gmt_create`,`gmt_modified`,`sex`,`birth`,`pic_id`,`live_address`,`hobby`,`province`,`city`,`district`,dept.`name` as deptName,u.`segment_code` as segmentCode
from sys_user u, sys_dept dept where u.dept_id = dept.dept_id
and u.username = #{username}
and u.password = #{password}
